inbound.set-body:
@{
            var deviceId = (string)context.Variables["deviceId"];
            var query = @"query ($deviceId: String!) {
                factory_iot_datas(
                    first: 1
                    filter: { DeviceID: { eq: $deviceId } }
                ) {
                    items {
                                Timestamp
                                BuildingID     
                                DeviceID
                                Location
                                MetricType
                                Value
                                Unit
                                Status
                    }
                }
            }";

            var requestBody = new {
                query = query,
                variables = new { 
                    deviceId = deviceId 
                }
            };

            return JsonConvert.SerializeObject(requestBody);
        }
