when.set-body:
@{
                    var responseBody = context.Response.Body.As();

                    if (responseBody["data"] != null && responseBody["data"]["factory_iot_datas"] != null)
                    {
                        var items = responseBody["data"]["factory_iot_datas"]["items"];
                        return JsonConvert.SerializeObject(new { sensors = items });
                    }
                    else if (responseBody["errors"] != null)
                    {
                        return JsonConvert.SerializeObject(new { 
                            error = "GraphQL Error",
                            details = responseBody["errors"]
                        });
                    }
                    else
                    {
                        return JsonConvert.SerializeObject(new { 
                            error = "Unexpected response format",
                            response = responseBody
                        });
                    }
                }

otherwise.set-body:
@{
                    return JsonConvert.SerializeObject(new { 
                        error = "Fabric API Error",
                        statusCode = context.Response.StatusCode,
                        statusReason = context.Response.StatusReason,
                        body = context.Response.Body.As()
                    });
                }
